import React from "react"

export default function Card(props) {
  let styleMode ={
    background: props.lightMode? "#FFF" : "#36454F",
    color: props.light? "#000" : "#FFF"
  }
  return (
    <div style={styleMode} className="card">
      {props.icon? props.icon : 
        <i className="fa fa-chevron-circle-up" 
          style={{color: props.iconColor? props.iconColor: "#3F75FE"}}></i> }
      <h3>{props.title? props.title : "Title Goes Here!"}</h3>
      <p>{props.description? props.description: "Description or any other relevant text will go here..."}</p>
    </div>
  )
}
