import React from "react"
import 'font-awesome/css/font-awesome.min.css'

export default function Banner(props) {
  const logoOptions = {success: <i className="fa fa-check-circle"></i>, 
    warning:<i className="fa fa-exclamation-triangle"></i>, error: <i className="fa fa-times-circle"></i>, 
      neutral: <i className="fa fa-info-circle"></i>}
  const logoColorOptions = {success:"#34D399", warning: "#FBBF24", error: "#F87171", neutral: "#60A5FA"}
  const messageOptions = {success: "Congratulations!", warning: "Attention", 
    error: "Error Occurred", neutral: "Update Available"}
  const backgroundOptions = {error: "#FEE2E2", warning: "#FEF3C7", success: "#D1FAE5", neutral: "#DBEAFE"}
  const colorOptions = {error: "#991B1B", warning: "#92400E", success: "#065F46", neutral: "#1E40AF"}
  const specificStyling = {
      color: colorOptions[props.type],
      background: backgroundOptions[props.type] 
  }

  return (
    <div className="banner" style={specificStyling}>

      <div className="banner-logo" style={{color: logoColorOptions[props.type]}}>
        {logoOptions[props.type]}
      </div>

      <div className="banner-text">
        <h3>
          {messageOptions[props.type]}
        </h3>
        {props.description? <p>{props.description}</p> : null}
      </div>

    </div>
  )
}
