import React, {useState} from "react"
import useEffectOnUpdate from "../hooks/useEffectOnUpdate.js"

export default function Card(props) {
  const [styleMode, setStyleMode] = useState({
    background: props.lightMode ? "#FFF" : "#36454F",
    color: props.lightMode ? "#000" : "#FFF"
  })

  // useEffectOnUpdate(toggleCard, [styleMode])

  // function toggleCard() {
  //   console.log("toggle func ran")
  //   setStyleMode({
  //     ...styleMode,
  //     animation: "cardReveal 1s"
  //   })
  // }

  const [clicked, setClicked] = useState(false);

  function toggleCard() {
    console.log("toggle func ran");
    setClicked(true);
  }

  useEffectOnUpdate(() => {
    if (clicked) {
      setStyleMode(prevStyleMode => ({
        ...prevStyleMode,
        animation: "cardReveal 1s"
      }));
    }
  }, [styleMode, clicked]);


  console.log("card rendered")

  return (
    <div style={styleMode} className="card" onClick={props.toggle? props.toggle : toggleCard}>
      {props.icon? props.icon : 
        <i className="fa fa-folder-open"
          style={{color: props.iconColor? props.iconColor: "#3F75FE"}}>
        </i> 
      }
      <h3>{props.title? props.title : "Discover More"}</h3>
      <p>{props.description? props.description : 
        "Start your design journey today with Designo! We are the leading provider in Market Consulting"}
      </p>
    </div>
  )
}
