import React from "react"
import 'font-awesome/css/font-awesome.min.css';

export default function Banner(props) {
  const specificStyling = {}
  const logoOptions = {success: `<i className="fas fa-check-circle"></i>`, 
    warning:`<i className="fas fa-exclamation-triangle"></i>`, error: `<i className="fas fa-times-circle"></i>`, 
      neutral: `<i className="fas fa-info-circle"></i>`}
  const messageOptions = ["success", "warning", "error", "neutral"]


  return (
    <div style={specificStyling}>

      <div>
        {Object.keys(logoOptions).forEach((current, key) => 
          current==props.type? logoOptions[key] : null) }
      </div>

      <div>
        <h3>
          {messageOptions.forEach((current, key)=> current===props.type? 
            messageOptions[key] : "message cannot be displayed" )}
        </h3>
        {props.description? <p>{props.description}</p> : null}
      </div>

    </div>
  )
}
