import React from "react"
const BadgeContext = React.createContext()

export default function Badge({children, colour, shape}) {
  const backgroundOptions = {gray: "#F3F4F6", red: "#FEE2E2", yellow: "#FEF3C7", 
    green: "#D1FAE5", blue: "#DBEAFE", indigo: "#E0E7FF", purple: "#EDE9FE", pink: "#FCE7F3"}
  const colorOptions = {gray: "#1F2937", red: "#991B1B", yellow: "#92400E", 
  green: "#065F46", blue: "#1E40AF", indigo: "#3730A3", purple: "#5B21B6", pink: "#9D174D"}
  let specificStyling = {}
  let defaultStyling = {
    borderRadius: "10px",
    fontSize: "1rem",
    textAlign: "center",
    fontFamily: "inter",
    width: "min-content",
  }


  if(colour) {
    specificStyling = {
      ...defaultStyling,
      background: 
        backgroundOptions[colour] || backgroundOptions["gray"],
      borderRadius: shape? "25px" : "5px",
      color: colorOptions[colour] || colorOptions["gray"]
    }
  }

  else {
    specificStyling = {
      ...defaultStyling,
      background: colorOptions["gray"],
      color: colorOptions["gray"]
    }
  }

  return (
    <div style={specificStyling}>
      {children}
    </div>
  )
}

export {BadgeContext}
