import React from "react"
const BadgeContext = React.createContext()

export default function Badge({children, colour, shape}) {
  const colourOptions = {gray: "#F3F4F6", red: "#FEE2E2", yellow: "#FEF3C7", 
    green: "#D1FAE5", blue: "#DBEAFE", indigo: "#E0E7FF", purple: "#EDE9FE", pink: "#FCE7F3"}
  let specificStyling = {}
  let defaultStyling = {
    borderRadius: "10px",
    fontSize: "1rem",
    textAlign: "center",
    fontFamily: "inter",
    width: "min-content",
  }


  if(colour && shape) {
    specificStyling = {
      ...defaultStyling,
      background: 
        colourOptions[colour] || "#FFF",
      borderRadius: "25px"
    }
  }

  else {
    specificStyling = {
      ...defaultStyling,
      background: colourOptions.red,
    }
  }

  return (
    <div style={specificStyling}>
      {children}
    </div>
  )
}

export {BadgeContext}
