import React from "react"
const BadgeContext = React.createContext()

export default function Badge(props) {
  const colourOptions = {gray: "#F3F4F6", red: "#FEE2E2", yellow: "#FEF3C7", 
    green: "#D1FAE5", blue: "#DBEAFE", indigo: "#E0E7FF", purple: "#EDE9FE", pink: "#FCE7F3"}
  let specificStyling = {}
  const defaultStyling = {
    borderRadius: "10px",
    fontSize: "1rem",
    textAlign: "center",
    fontFamily: "inter"
  }

  if(props.colour && props.shape) {
    specificStyling = {
      ...defaultStyling,
      backgroundColor: 
        Object.keys(colourOptions).forEach((current, key) => {
          if(current===props.colour) 
            return colourOptions[key]}),
      borderRadius: "25px"
    }
  }

  else {
    specificStyling = {
      ...defaultStyling,
      backgroundColor: colourOptions.gray,
    }
  }

  return (
    <div style={specificStyling}>
      {/* children */}
    </div>
  )
}

export {BadgeContext}
