import React from "react";
import 'font-awesome/css/font-awesome.min.css';

export default function Banner(props) {
  const bannerTypes = {
    success: {
      logo: <i className="fa fa-check-circle"></i>,
      message: "Congratulations!",
      background: "#D1FAE5",
      color: "#065F46"
    },
    warning: {
      logo: <i className="fa fa-exclamation-triangle"></i>,
      message: "Attention",
      background: "#FEF3C7",
      color: "#92400E"
    },
    error: {
      logo: <i className="fa fa-times-circle"></i>,
      message: "Error Occurred",
      background: "#FEE2E2",
      color: "#991B1B"
    },
    neutral: {
      logo: <i className="fa fa-info-circle"></i>,
      message: "Update Available",
      background: "#DBEAFE",
      color: "#1E40AF"
    }
  };

  const { logo, message, background, color } = bannerTypes[props.type];

  return (
    <div className="banner" style={{ background, color }}>
      <div className="banner-logo" style={{ color }}>
        {logo}
      </div>
      <div className="banner-text">
        <h3>{message}</h3>
        {props.description ? <p>{props.description}</p> : null}
      </div>
    </div>
  );
}
